import sqlite3db_path = r"D:/Artem Python СР-СБ 17 00 Папка для работ/Bot/inventory.db"def create_inventory_table():    conn = sqlite3.connect(db_path)    cursor = conn.cursor()    cursor.execute('''    CREATE TABLE IF NOT EXISTS inventory (        id INTEGER PRIMARY KEY AUTOINCREMENT,        name TEXT NOT NULL,        unit TEXT NOT NULL    )    ''')    conn.commit()    conn.close()def insert_items():    items = [        ("Ответная планка (Накладка) 153/155", "шт"),        ("Петля ПВХ 75", "шт"),        ("Ручка дверная 'Д'", "шт"),        ("Петля ПВХ 100", "шт"),        ("Петля Алдокс", "шт"),        ("Петля Алдокс 3", "шт"),        ("Петля Дверная балкон", "шт"),        ("Ручка дверная 28", "шт"),        ("Ручка дверная Фортуна", "шт"),        ("Ручка дверная Фортуна мини", "шт"),        ("Ручка дверная ЛЮКС до 50см", "шт"),        ("Ручка дверная ЛЮКС (TUR)", "шт"),        ("Ручка оконная ДЕЛЬФИН", "шт"),        ("Ручка оконная ПВХ", "шт"),        ("Шпингалет внутренний", "шт"),        ("Защелка Чит чит", "шт"),        ("2330 Ножка", "шт"),        ("2330 Труба (за 1м)", "шт"),        ("Клипсы под стеклопакет (500шт)", "пачка"),        ("Фиксатор пим Алдокс - 'шок' (200шт)", "шт"),        ("Соединитель 151 - 'ёлка'", "шт"),        ("Соединитель 155 - 'ёлка'", "шт"),        ("Соединитель АХТ - ТЕРМО", "шт"),        ("ПВХ Зацеп 5200 / 6000", "шт"),        ("ПВХ Соединитель 5200 / 6000", "шт")    ]    conn = sqlite3.connect(db_path)    cursor = conn.cursor()    cursor.executemany('INSERT INTO inventory (name, unit) VALUES (?, ?)', items)    conn.commit()    conn.close()create_inventory_table()insert_items()